import React, {useState, useEffect} from 'react'
import resultService from './services/results'

const ResultInput = (props) => {
  const date = props.date
  const setDate = props.setDate
  const winner = props.winner
  const setWinner = props.setWinner
  const players = props.players
  const setPlayers = props.setPlayers

  const addResult = (event) => {
    event.preventdefault()
    console.log(date, players, winner);
    resultService.postResult({date, players, winner})
  }

  return(
    <div>
      <h2>Add New Results</h2>
      <form onSubmit={addResult}>
        <div>
          Date: 
          <input 
            type="date"
            id='date' 
            value={date} 
            onChange={({target})=>setDate(target.value)}
          />
        </div>
        <div>
          Winner:
          <input 
            type="text"
            id='winner' 
            value={winner} 
            onChange={({target})=>setWinner(target.value)}
          />
          </div>
          <div>
          # of Players
            <input 
              type="number" 
              id='players' 
              value={players} 
              onChange={({target})=>setPlayers(target.value)}
            />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  )
}

const App = () => {
  const [results, setResults] = useState([]) 
  const [winner, setWinner] = useState('') 
  const [date, setDate] = useState('') 
  const [players, setPlayers] = useState('') 

  useEffect(()=>{
    resultService.getAll()
      .then(res => setResults(res))
  },[])

  const RecentGames = () => {
    return(
      <div>
        <h2>Past Games</h2>
        {
          results.map(g =>{
            return(
              <div key={g.id}>
                <h3>{`Game on ${g.date}`}</h3>
                <p>{`Winner: ${g.winner}`}</p>
              </div>
            )
          })
        }
      </div>
    )
  }

  return (
    <div>
      <h2>Welcome to MKCharts</h2>
      <div>
        <ResultInput
          data={date}
          setDate={setDate}
          winner={winner}
          setWinner={setWinner}
          players={players}
          setPlayers={setPlayers}
        />
      </div>
      <div>
        <RecentGames/>
      </div>
    </div>
  )

  }

export default App;
