  const [date, setDate] = useState('') 
  const [players, setPlayers] = useState('') 

  useEffect(()=>{
    resultService.getAll().then(res => setResults(res))
  },[])

  const RecentGames = () => {
    return(
      <div>
        <h2>Past Games</h2>
        {
          results.map(g =>{
            return(
              <div>
                <h3>{`Game on ${g.date}`}</h3>
                <li>{`Winner: ${g.winner}`}</li>
