    // }
    // else if (stateName === 'players'){
    //   setInput({...input , players: value})
    // }

  }

  const ResultInput = () => {
    console.log(date,winner,players);
    return(
      <div>
        <h2>Add New Results</h2>
        <form onSubmit={addResult}>
        <input type="date" id='date' value={date} onChange={(target)=>setDate(target.value)}/>
        <input type="text" id='winner' value={winner} onChange={(target)=>setWinner(target.value)}/>
        <input type="number" id='players' value={players} onChange={(target)=>setPlayers(target.value)}/>
          <button type="submit">Submit</button>
        </form>
      </div>
    )
  }


  return (
    <div>
      <h2>Welcome to MKCharts</h2>
      <RecentGames/>
      <ResultInput/>
    </div>
  );
}

export default App;
