import React, {useState, useEffect} from 'react'
import resultService from './services/results'

const ResultInput = (props) => {
  const date = props.date
  const setDate = props.setDate
  const winner = props.winner
  const setWinner = props.setWinner
  const players = props.players
  const setPlayers = props.setPlayers


  const addResult = (event) => {
    event.preventdefault()
    console.log("submitted");
  }

  const resultState = (event) => {
    const stateName = event.target.id
    const value = event.target.value

    // if (stateName==='date'){
    //   setInput({...input , date: value})
    // }
    // else if (stateName==='winner'){
    //   setInput({...input , winner: value})
    //   console.log(input);
    // }
    // else if (stateName === 'players'){
    //   setInput({...input , players: value})
    // }
  }


  return(
    <div>
      <h2>Add New Results</h2>
      <form onSubmit={addResult}>
        <div>
          <input 
            value={date} 
            onChange={({target})=>setDate(target.value)}
          />
          <input 
            name='winner' 
            type="text"
            id='winner' 
            value={winner} 
            onChange={({target})=>setWinner(target.value)}
          />
          <input 
            name='players'
            type="text" 
            id='players' 
            value={players} 
            onChange={({target})=>setPlayers(target.value)}
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  )
}

const App = () => {
  const [results, setResults] = useState([]) 
  const [winner, setWinner] = useState('') 
  const [date, setDate] = useState('') 
  const [players, setPlayers] = useState('') 

  useEffect(()=>{
    resultService.getAll()
      .then(res => setResults(res))
  },[])

  const RecentGames = () => {
    return(
      <div>
        <h2>Past Games</h2>
        {
          results.map(g =>{
            return(
              <div>
                <h3>{`Game on ${g.date}`}</h3>
                <li>{`Winner: ${g.winner}`}</li>
              </div>
            )
          })
        }
      </div>
    )
  }

  const addResult = (event) => {
    event.preventdefault()
    console.log("submitted");
  }

  return (
    <div>
      <h2>Welcome to MKCharts</h2>
      <div>
        <ResultInput
          data={date}
          setDate={setDate}
          winner={winner}
          setWinner={setWinner}
          players={players}
          setPlayers={setPlayers}
        />
      </div>
    </div>
  )

  }

export default App;
