import React, {useState, useEffect} from 'react'
import resultService from './services/results'

const ResultInput = ({date, newDate, winner, setWinner, players, setPlayers}) => {
  const getId = () => (100000 * Math.random()).toFixed(0)
  const asObject = (date, players, winner) => {
    return {
      date,
      players,
      winner,
      id: getId()
    }
  }

  const addResult = (event) => {
    event.preventDefault()

    const newResult = asObject(date, players, winner)
    resultService.postResult(newResult)
  }

  return(
    <div>
      <h2>Add New Results</h2>
      <form onSubmit={addResult}>
        <div>
          Date: 
          <input 
            name="date"
            type="date"
            id='date' 
            value={date} 
            onChange={({target})=>newDate(target.value)}
          />
        </div>
        <div>
          Winner:
          <input 
            name="winner"
            type="text"
            id='winner' 
            value={winner} 
            onChange={({target})=>setWinner(target.value)}
          />
          </div>
          <div>
          # of Players
            <input 
              name="players"
              type="number" 
              id='players' 
              value={players} 
              onChange={({target})=>setPlayers(target.value)}
            />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  )
}

const App = () => {
  const [results, setResults] = useState([]) 
  const [winner, setWinner] = useState('') 
  const [date, newDate] = useState('') 
  const [players, setPlayers] = useState('') 

  useEffect(()=>{
    resultService.getAll()
      .then(res => setResults(res))
  },[])

  const RecentGames = () => {
    return(
      <div>
        <h2>Past Games</h2>
        <h3>WINNERS</h3>
        {
          results.map(g =>{
            return(
              <div key={g.id}>
                <b>{`${g.winner}`}</b>
                {`Game on ${g.date}`}
              </div>
            )
          })
        }
      </div>
    )
  }

  return (
    <div>
      <h2>Welcome to MKCharts</h2>
      <div>
        <ResultInput
          date={date}
          newDate={newDate}
          winner={winner}
          setWinner={setWinner}
          players={players}
          setPlayers={setPlayers}
        />
      </div>
      <div>
        <RecentGames/>
      </div>
    </div>
  )

  }

export default App;
